import java.io.* 
import jxl.* 
import jxl.write.* 
import java.text.SimpleDateFormat

//get 
log.info("=======================test started for get=======================") 

def f_g=new File("D:/rest-demo/my-app/sampleGetCustomerByIdData.xls"); //97-2013 workbook xls 
def wk_g= Workbook.getWorkbook(f_g) 
def ws_g=wk_g.getSheet(0) 
r_g=ws_g.getRows()	//max row number
log.info "Total row number - "+r_g   

for(def i=1;i<r_g;i++) 
{     

	log.info "Row number - "+i     
	Cell c1=ws_g.getCell(0,i)     
	log.info c1.getContents()          
	def get = new URL("http://rest-api.upskills.in/api/rest_admin/customers/"+c1.getContents()).openConnection();
	get.setRequestProperty("Authorization", "Bearer 17c17f4620a58beda5218768bb2c7473a5761e40") 
	def getRC = get.getResponseCode();     
	log.info("res code is - "+getRC);     
	if(getRC.equals(200)) {   
		log.info(get.getInputStream().getText());     
	} else {
		log.info("Something went wrong");
	}

}
